import { useEffect, useState } from "react";
import styled from "styled-components";

const Input = styled.input`
  border: 0;
  outline: none;
  font-size: 18px;
  color: black;
`;
const Form = styled.form`
  border: 1px solid #666;
  padding: 1rem;
  border-radius: 10px;
  display: grid;
  grid-template-columns: 1fr 150px;
  gap: 15px;
`;

const Button = styled.button`
  border: none;
  background: #db4c3f;
  padding: 1rem 0;
  border-radius: 10px;
  color: white;
  font-weight: bold;
`;

const Add = () => {
  const [title , setTitle] = useState("")

  const getChange = (e)=>{
    setTitle(e.target.value)
  }

  const onSubmit = (e) =>{
    e.preventDefault();
    saveData();
  }

  const saveData = async () =>{
    let request = {
      method: "POST",
      body: JSON.stringify({title: title, completed: false}),
      'headers':{
        'Content-Type': 'application/json'
      }
    }
    const todo = await fetch("http://localhost:3004/posts", request);
    const result = await todo.json();
    console.log(result, 'resultado') 

  }

  useEffect ( ()=>{

  }, []);


  return (
    <Form>
      <Input onChange={getChange} type="text" placeholder="Nombre de la tarea" />
      <Button onClick={onSubmit}>Agregar tarea</Button>
    </Form>
  );
};

export default Add;
