import { useEffect, useState } from "react";
import styled from "styled-components";
import Add from "../components/add";
import Item from "../components/item";
import Layout from "../components/layout";
import Title from "../components/title";


const Cont = styled.div`
  margin-top: 1rem;
`;
const Subtitle = styled.p`
  margin: 2rem 1rem 1rem 1rem;
`;

const Index = () => {
  const [ list, setList] = useState([])
  const [ complete, setComplete] = useState([])

  const getData = async() =>{
    const todo = await fetch("https://630cc4fb83986f74a7ca0f27.mockapi.io/todo?completed=false");
    const result = await todo.json();
    console.log(result, 'resultado')
    setList(result)
  }

  const getDataComplete = async() =>{
    const todo = await fetch("https://630cc4fb83986f74a7ca0f27.mockapi.io/todo?completed=true");
    const result = await todo.json();
    console.log(result, 'resultado')
    setComplete(result)
  }



  useEffect (()=>{
    getData();
    getDataComplete();
  }, []);

  return (
    <>
      <Layout>
        <div className="container">
          <Title>Todo List</Title>
          <Add></Add>

          <Cont>
            {list.map((val, key) => {
              return (
                <Item key={key} data={val}> 
                </Item>
              );
            })}
            <Subtitle>Completados: </Subtitle>
            {complete.map((val, key) => {
              return (
                <Item key={key} data={val}> 
                </Item>
              );
            })}
          </Cont>
        </div>
      </Layout>
    </>
  );
};

export default Index;
