import { useEffect, useState } from "react";
import styled from "styled-components";
import Add from "../components/add";
import Item from "../components/item";
import Layout from "../components/layout";
import Title from "../components/title";

const Cont = styled.div`
  margin-top: 1rem;
`;
const Subtitle = styled.p`
  margin: 2rem 1rem 1rem 1rem;
  color: #666;
`;

const Index = () => {
  const [list, setList] = useState([]);
  const [title, setTitle] = useState("");
  const [id, setID] = useState("");

  const [complete, setComplete] = useState([]);
  const API = "http://localhost:3004";

  const getData = async () => {
    const response = await fetch(`${API}/posts?completed=false`);
    const output = await response.json();
    console.log(output, "outputado");
    setList(output);
  };

  const getDataComplete = async () => {
    const response = await fetch(`${API}/posts?completed=true`);
    const output = await response.json();
    console.log(output, "outputado");
    setComplete(output);
  };

  const saveData = async (title) => {
    let request = {
      method: "POST",
      body: JSON.stringify({ title: title, completed: false }),
      headers: {
        "Content-Type": "application/json",
      },
    };
    await fetch(`${API}/posts`, request);
    getData();
  };

  const updateData = async (info) => {
    console.log("info", info);
    let data = { title: info.title }
    if(!id){
      data.completed = !info.completed
    }
    console.log(data)
    let request = {
      method: "PUT",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
    };
    await fetch(`${API}/posts/${info.id}`, request);
    getData();
    getDataComplete();
  };

  const deleteData = async (info) => {
    let request = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    };
    await fetch(`${API}/posts/${info.id}`, request);
    getData();
    getDataComplete();
  };

  const editText = (info) => {
    console.log("click");
    setTitle(info.title);
    setID(info.id);
  };

  const getChange = (e) => {
    setTitle(e.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if(id){
      updateData({title: title, id: id});
      setTitle("");
      setID("");
    }
    else{
      saveData(title);
      setID("");
      setTitle("");

    }
  };

  useEffect(() => {
    getData();
    getDataComplete();
  }, []);

  return (
    <>
      <Layout>
        <div className="container">
          <Title>Todo List</Title>

          <Add
            title={title}
            onSubmit={onSubmit}
            getChange={getChange} 
          ></Add>

          <Cont>
            {list.map((val, key) => {
              return (
                <Item
                  key={key}
                  data={val}
                  editText={editText}
                  updateData={updateData}
                  deleteData={deleteData}
                ></Item>
              );
            })}
            <Subtitle>Completados: </Subtitle>
            {complete.map((val, key) => {
              return (
                <Item
                  key={key}
                  data={val}
                  editText={editText}
                  updateData={updateData}
                  deleteData={deleteData}
                ></Item>
              );
            })}
          </Cont>
        </div>
      </Layout>
    </>
  );
};

export default Index;
