// import styled from "styled-components";

// const Wrapper = styled.div`
//   background: red;
//   text-align: center;
//   margin: 0 auto;
//   height: 100vh;
// `;

import styled from "styled-components";
import Layout from "../components/layout";
import Title from "../components/title";

const Input = styled.input`
  border: 0;
  outline: none;
  font-size: 18px;
  color: black;
`;
const Form = styled.form`
  border: 1px solid gray;
  padding: 1rem;
  border-radius: 10px;
  display: grid;
  grid-template-columns: 1fr 150px;
  gap: 15px;
`;

const Button = styled.button`
  border: none;
  background: #db4c3f;
  padding: 1rem 0;
  border-radius: 10px;
  color: white;
  font-weight: bold;
`;

const Item = styled.div`
  border-bottom: 1px solid #f5f5f5;
  display: grid;
  grid-template-columns: 1fr 80px;
  align-items: center;
  padding: 0 1rem;
  justify-content: center;

  &:hover {
    background: #f5f5f5;
  }
  .input-group {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  [type="radio"]:checked,
  [type="radio"]:not(:checked) {
    position: absolute;
    left: -9999px;
  }
  [type="radio"]:checked + label,
  [type="radio"]:not(:checked) + label {
    position: relative;
    padding-left: 28px;
    cursor: pointer;
    line-height: 20px;
    display: inline-block;
    color: #666;
  }
  [type="radio"]:checked + label:before,
  [type="radio"]:not(:checked) + label:before {
    content: "";
    position: absolute;
    left: 0;
    top: 50%;
    width: 18px;
    height: 18px;
    border: 1px solid #666;
    border-radius: 100%;
    background: #fff;
    transform: translate(0, -50%);
  }
  [type="radio"]:checked + label:after,
  [type="radio"]:not(:checked) + label:after {
    content: "";
    width: 12px;
    height: 12px;
    background: #db4c3f;
    position: absolute;
    top: 20px;
    left: 4px;
    border-radius: 100%;
    -webkit-transition: all 0.2s ease;
    transition: all 0.2s ease;
    transform: translate(0, -50%);
  }
  [type="radio"]:not(:checked) + label:after {
    opacity: 0;
    -webkit-transform: scale(0);
    transform: scale(0);
  }
  [type="radio"]:checked + label:after {
    opacity: 1;
    -webkit-transform: scale(1);
    transform: scale(1);
  }

  label {
    font-size: 18px;
    display: block;
    width: 100%;
    padding: 1rem 0;
  }
  .actions{
    display: flex;
    justify-content: end;
  }
`;

const Cont = styled.div`
  margin-top: 1rem;
`;

const Index = () => {
  return (
    <>
      <Layout>
        <div className="container">
          <Title>Todo List</Title>
          <Form>
            <Input type="text" placeholder="Agregar" />
            <Button>Agregar tarea</Button>
          </Form>

          <Cont>
            {new Array(10).fill({ val: 1 }).map((val, key) => {
              return (
                <Item key={key}>
                  <div className="input-group">
                    <input id={`check-${key}`} type="radio" />
                    <label htmlFor={`check-${key}`}>Titulo</label>
                  </div>
                  <div className="actions">
                    <span class="material-symbols-outlined">delete</span>
                  </div>
                </Item>
              );
            })}
          </Cont>
        </div>
      </Layout>
    </>
  );
};

export default Index;
