import { useEffect, useState } from "react";
import styled from "styled-components";
import Add from "../components/add";
import Item from "../components/item";
import Layout from "../components/layout";
import Title from "../components/title";


const Cont = styled.div`
  margin-top: 1rem;
`;
const Subtitle = styled.p`
  margin: 2rem 1rem 1rem 1rem;
  color: #666;
`;

const Index = () => {
  const [ list, setList] = useState([])
  const [ complete, setComplete] = useState([])
  const API = 'http://localhost:3004';

  const getData = async() =>{
    const response = await fetch(`${API}/posts?completed=false`);
    const output = await response.json();
    console.log(output, 'outputado')
    setList(output)
  }

  const getDataComplete = async() =>{
    const response = await fetch(`${API}/posts?completed=true`);
    const output = await response.json();
    console.log(output, 'outputado')
    setComplete(output)
  }

  const saveData = async (title) =>{
    let request = {
      method: "POST",
      body: JSON.stringify({title: title, completed: false}),
      'headers':{
        'Content-Type': 'application/json'
      }
    }
    await fetch(`${API}/posts`, request); 
    getData();
  }

  const updateData = async (info) =>{
    console.log('info', info)
    let request = {
      method: "PUT",
      body: JSON.stringify({title: info.title, completed: !info.completed}),
      'headers':{
        'Content-Type': 'application/json'
      }
    }
    await fetch(`${API}/posts/${info.id}`, request); 
    getData();
    getDataComplete();
  }

  const deleteData = async (info) =>{ 
    let request = {
      method: "DELETE", 
      'headers':{
        'Content-Type': 'application/json'
      }
    }
    await fetch(`${API}/posts/${info.id}`, request); 
    getData();
    getDataComplete();
  }

  const pastText = () =>{

  }
  const editText = (info) =>{
    localStorage.setItem('ID', info.id)
    localStorage.setItem('TITLE', info.title)
  }

  useEffect (()=>{
    getData();
    getDataComplete();
  }, []);

  return (
    <>
      <Layout>
        <div className="container">
          <Title>Todo List</Title>
          <Add saveData={saveData} pastText={pastText}></Add>

          <Cont>
            {list.map((val, key) => {
              return (
                <Item key={key} data={val}  updateData={updateData} editText={editText} deleteData={deleteData}> 
                </Item>
              );
            })}
            <Subtitle>Completados: </Subtitle>
            {complete.map((val, key) => {
              return (
                <Item key={key} data={val} updateData={updateData} deleteData={deleteData}> 
                </Item>
              );
            })}
          </Cont>
        </div>
      </Layout>
    </>
  );
};

export default Index;
