// import styled from "styled-components";

// const Wrapper = styled.div`
//   background: red;
//   text-align: center;
//   margin: 0 auto;
//   height: 100vh;
// `;

import styled from "styled-components";
import Layout from "../components/layout";
import Title from "../components/title";

const Input = styled.input`
  border: 0;
  outline: none;
  font-size: 18px;
  color: black;
`;
const Form = styled.form`
  border: 1px solid gray;
  padding: 1rem;
  border-radius: 10px;
  display: grid;
  grid-template-columns: 1fr 150px;
  gap: 15px;
`;

const Button = styled.button`
  border: none;
  background: #db4c3f;
  padding: 1rem 0;
  border-radius: 10px;
  color: white;
  font-weight: bold;
`;

const Item = styled.div`
  border-bottom: 1px solid #ccc;
  display: grid;
  grid-template-columns: 50px 1fr 80px;
  padding: 1rem 0;
  align-items: center;
  justify-content: center;
  padding: 1rem;
  &:hover{
    background: red;
  }
  input{
    font-size: 40px;
  }
  span{
    font-size: 18px;
  }
`

const Cont = styled.div`
  margin-top: 1rem;
`

const Index = () => {
  return (
    <>
      <Layout>
        <div className="container">
          <Title>Todo List</Title>
          <Form>
            <Input type="text" placeholder="Agregar" />
            <Button>Agregar tarea</Button>
          </Form>

          <Cont>
            {new Array(10).fill({ val: 1 }).map(() => {
              return (
                <Item>
                  <input type="radio" />
                  <span>Titulo</span>
                  <div className="actions">Delete</div>
                </Item>
              );
            })}
          </Cont>
        </div>
      </Layout>
    </>
  );
};

export default Index;
