import { useEffect, useState } from "react";
import styled from "styled-components";

const Input = styled.input`
  border: 0;
  outline: none;
  font-size: 18px;
  color: black;
`;
const Form = styled.form`
  border: 1px solid #666;
  padding: 1rem;
  border-radius: 10px;
  display: grid;
  grid-template-columns: 1fr 150px;
  gap: 15px;
`;

const Button = styled.button`
  border: none;
  background: #db4c3f;
  padding: 1rem 0;
  border-radius: 10px;
  color: white;
  font-weight: bold;
  cursor: pointer;
`;

const Add = (props) => {
  // localStorage.getItem('TITLE') 
  // localStorage.getItem('ID') 
  const [title , setTitle] = useState(props.title || '')
  const [id , setID] = useState('')

  const getChange = (e)=>{
    setTitle(e.target.value)
  }

  const onSubmit = (e) =>{
    e.preventDefault();
    props.saveData(title);
    setTitle('');
  }

  const pastText = (text)=>{
    setTitle(text.title)
    setID(text.id)
  }  

  useEffect ( ()=>{

  }, []);


  return (
    <Form>
      <Input value={title} onChange={getChange} type="text" placeholder="Nombre de la tarea" />
      <Button onClick={onSubmit} disabled={title.length > 0 ? false: true}>Agregar tarea</Button>
    </Form>
  );
};

export default Add;
