import { useEffect, useState } from "react";
import styled from "styled-components";
import Add from "../components/add";
import Item from "../components/item";
import Layout from "../components/layout";
import Title from "../components/title";

const Cont = styled.div`
  margin-top: 1rem;
`;
const Subtitle = styled.p`
  margin: 2rem 1rem 1rem 1rem;
  color: #666;
`;

const Index = () => {
  const [list, setList] = useState([]);
  const [title, setTitle] = useState("");
  const [info, setInfo] = useState({title: "", completed: false});
  const [id, setID] = useState("");

  const [complete, setComplete] = useState([]);
  const API = "http://localhost:3004";

  const getData = async () => {
    const response = await fetch(`${API}/posts?completed=false`);
    const output = await response.json();
    console.log(output, "outputado");
    setList(output);
  };

  const getDataComplete = async () => {
    const response = await fetch(`${API}/posts?completed=true`);
    const output = await response.json();
    console.log(output, "outputado");
    setComplete(output);
  };

  const saveData = async () => {
    let request = {
      method: "POST",
      body: JSON.stringify({ title: info.title, completed: false }),
      headers: {
        "Content-Type": "application/json",
      },
    };
    await fetch(`${API}/posts`, request);
    getData();
  };

  const updateData = async (response) => {
    let item = response
    if(!info.id){
      item.completed = !item.completed
    } 
    let request = {
      method: "PUT",
      body: JSON.stringify(item),
      headers: {
        "Content-Type": "application/json",
      },
    };
    await fetch(`${API}/posts/${response.id}`, request);
    getData();
    getDataComplete();
  };

  const deleteData = async (info) => {
    let request = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    };
    await fetch(`${API}/posts/${info.id}`, request);
    getData();
    getDataComplete();
  };

  const editText = (info) => {
    console.log("click");
    setInfo(info);
  };

  const getChange = (e) => {
    setInfo({...info, title: e.target.value});
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if(info.id){
      updateData({title: info.title, id: info.id, completed: info.completed});
      setInfo({title: "", completed: false});
    }
    else{
      saveData(title);
      setInfo({title: "", completed: false});

    }
  };

  useEffect(() => {
    getData();
    getDataComplete();
  }, []);

  return (
    <>
      <Layout>
        <div className="container">
          <Title>Todo List</Title>
          {JSON.stringify(info, null ,2 )}
          <Add
            title={info.title}
            onSubmit={onSubmit}
            getChange={getChange} 
          ></Add>

          <Cont>
            {list.map((val, key) => {
              return (
                <Item
                  key={key}
                  data={val}
                  editText={editText}
                  updateData={updateData}
                  deleteData={deleteData}
                ></Item>
              );
            })}
            <Subtitle>Completados: </Subtitle>
            {complete.map((val, key) => {
              return (
                <Item
                  key={key}
                  data={val}
                  editText={editText}
                  updateData={updateData}
                  deleteData={deleteData}
                ></Item>
              );
            })}
          </Cont>
        </div>
      </Layout>
    </>
  );
};

export default Index;
